# time O(n * m), space O(1)
def min_window(str1, str2):
    size1, size2 = len(str1), len(str2)
    min_sub_length = float('inf')
    index1, index2 = 0, 0
    min_subsequence = ""
    
    while index1 < size1:
        # if current character of str1, pointed at by index1, and current str2 character, pointed at by index2 are the same
        if str1[index1] == str2[index2]:
            # if pointed character same in both strings, increment index2
            index2 += 1
            # check if valid substring has been found
            if index2 == size2:
                start, end = index1, index1
                index2 -= 1
                # decrement pointer index2 and start reverse loop 
                while index2 >= 0:
                    # decrement pointer index2 until all str2 characters found in str1 
                    if str1[start] == str2[index2]:
                        index2 -= 1
                    start -= 1
                start += 1 
                # update minimum substring length and minimum subsequence if current subsequence is shorter 
                if end - start < min_sub_length:
                    min_sub_length = end - start
                    min_subsequence = str1[start : end + 1]
                # set index1 to start to continue checking in str1 after this discovered subsequence 
                index1 = start
                index2 = 0
        # increment pointer index1 to check next character in str1
        index1 += 1
    return min_subsequence

def main():
    str1 = ["abcdedeaqdweq", "fgrqsqsnodwmxzkzxwqegkndaa", "zxcvnhss", "alpha", "beta"]
    str2 = ["adeq", "kzed", "css", "la", "ab"]

    # let's uncomment the same test case to see the difference
    str1.append("")
    str2.append("fffessa")

    for i in range(len(str1)):
        print(i+1, ". \tInput string: (" + str1[i]+", " + str2[i]+")", sep="")
        print("\tSubsequence string: ", min_window(str1[i], str2[i]))
        print("-"*100)

if __name__ == '__main__':
    main()